import React from 'react';
import Person from './components/Person'
import axios from 'axios'

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      persons: props.persons,
      newName: 'Lisää henkilö',
      newNumber: 'Ja numero'
    }
  }
  //uuden henkilön lisäys
  addPerson = (event) => {
        event.preventDefault()
        const personObject = {
          name: this.state.newName,
          number: this.state.newNumber,
          id: this.state.persons.length + 1,
          remove: false
        }

        //katotaan onko jo listalla, lisätään jos ei
        var a=true;
        var i=0;
      while(i < this.state.persons.length && a) {  
        if(this.state.newName !== this.state.persons[i].name){
          axios
          .post('http://localhost:3001/persons', personObject)
          .then(response => {
            this.setState({
              persons: this.state.persons.concat(response.data),
              newPerson: ''
            })
          })
        }
        else{
          alert("Nimi on jo listalla!")
          a=false;
        } i++;
      }
    }

componentWillMount() {
  console.log('will mount')
  axios
    .get('http://localhost:3001/persons')
    .then(response => {
      console.log('promise fulfilled')
      this.setState({ persons: response.data })
    })
}  
toggleRemoveOf = (id) => {
  return () => {
    const person = this.state.persons.find(p => p.id === id)
    const changedPerson = { ...person, remove: !person.remove }

    personService
      .update(id, changedPerson)
      .then(changedPerson => {
        this.setState({
          persons: this.state.persons.map(person => person.id !== id ? person : changedPerson)
        })
      })
      .catch(error => {
        alert(`Henkilö '${person.content}' on jo valitettavasti poistettu palvelimelta`)
        this.setState({ persons: this.state.persons.filter(n => p.id !== id) })
      })
  }
}

  handleNewName = (event) => {
    console.log(event.target.value)
    this.setState({ newName: event.target.value })
  }
  handleNewNumber= (event) => {
    console.log(event.target.value)
    this.setState({ newNumber: event.target.value })
  }

  render() {
    return (
      <div>
        <h2>Puhelinluettelo</h2>
        <form onSubmit={this.addPerson}>
          <label>Nimi:</label>
          <input 
            value={this.state.newName} 
            onChange={this.handleNewName}
          />
          <label>Numero:</label>
          <input 
            value={this.state.newNumber} 
            onChange={this.handleNewNumber}
          />
          <div>
          <button type="submit">Lisää</button>
          </div>
        </form>

        <h2>Numerot</h2>
        <div>
          {this.state.persons.map(person => <Person key={person.id} person={person} />)}
        </div>
      </div>
    )
  }
}

export default App
