import React from 'react';
import Person from './components/Person'
import personService from './services/persons'

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      persons: props.persons,
      newName: 'Lisää henkilö',
      newNumber: 'Ja numero'
    }
  }
  componentWillMount() {
    personService
      .getAll()
      .then(persons => {
        this.setState({ persons })
      })
  }
  toggleVisible = () => {
    this.setState({ showAll: !this.state.showAll })
  }

  //uuden henkilön lisäys
  addPerson = (event) => {
        event.preventDefault()
        const personObject = {
          name: this.state.newName,
          number: this.state.newNumber,
          id: this.state.persons.length + 1,
          remove: false
        }

        //katotaan onko jo listalla, kasvatetaan i jos ei ja sitten 
        // jos i=listan pituus, niin lisätään henkilö listalle
        var i=0; 
      if(this.state.newName !== this.state.persons[i].name && i < this.state.persons.length){
          i++;
          if(i===this.state.persons.length){
            personService
            .create(personObject)
            .then(newPerson => {
              this.setState({
                persons: this.state.persons.concat(newPerson),
                newPerson: 'A'
              })
            })
          }
        }

      else{
          alert("Nimi on jo listalla!")
        }
    }

  toggleRemove = (id) => {
    return () => {
      const person = this.state.persons.find(p => p.id === id)
      const changedPerson = { ...person, remove: !person.remove }

      personService
        .update(id, changedPerson)
        .then(changedPerson => {
          this.setState({
            persons: this.state.persons.map(person => person.id !== id ? person : changedPerson)
          })
        })
        .catch(error => {
          alert(`Henkilö '${person.content}' on jo valitettavasti poistettu palvelimelta`)
          this.setState({ persons: this.state.persons.filter(p => p.id !== id) })
        })
    }
  }

  handleNewName = (event) => {
    console.log(event.target.value)
    this.setState({ newName: event.target.value })
  }
  handleNewNumber= (event) => {
    console.log(event.target.value)
    this.setState({ newNumber: event.target.value })
  }

  render() {
    return (
      <div>
        <h2>Puhelinluettelo</h2>
        <form onSubmit={this.addPerson}>
          <label>Nimi:</label>
          <input 
            value={this.state.newName} 
            onChange={this.handleNewName}
          />
          <label>Numero:</label>
          <input 
            value={this.state.newNumber} 
            onChange={this.handleNewNumber}
          />
          <div>
          <button type="submit">Lisää</button>
          </div>
        </form>

        <h2>Numerot</h2>
        <div>
          {this.state.persons.map(person => <Person key={person.id} person={person} />)}
        </div>
      </div>
    )
  }
}

export default App
