import React from 'react';
import Person from './components/Person'

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      persons: props.persons,
      newPerson: 'Lisää henkilö'
    }
  }

  addPerson = (event) => {
    event.preventDefault()
    const personObject = {
      name: this.state.newPerson,
      id: this.state.persons.length + 1
    }

    const persons = this.state.persons.concat(personObject)

    this.setState({
      persons,
      newPerson: ''
    })
  }

  handleNewPerson = (event) => {
    console.log(event.target.value)
    this.setState({ newPerson: event.target.value })
  }

  render() {
    return (
      <div>
        <h2>Puhelinluettelo</h2>
        <form onSubmit={this.addPerson}>
          <input 
            value={this.state.newPerson} 
            onChange={this.handleNewPerson}
          />
          <button type="submit">tallenna</button>
        </form>
        <h2>Numerot</h2>
        <ul>
          {this.state.persons.map(person => <Person key={person.id} person={person} />)}
        </ul>
      </div>
    )
  }
}

export default App
