import React from 'react';
import Note from './components/Note'

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      persons: props.persons,
      newPerson: 'Lisää henkilö'
    }
  }

  addPerson = (event) => {
    event.preventDefault()
    const noteObject = {
      name: this.state.newPerson,
      id: this.state.persons.length + 1
    }

    const persons = this.state.persons.concat(noteObject)

    this.setState({
      persons,
      newPerson: ''
    })
  }

  handleNoteChange = (event) => {
    console.log(event.target.value)
    this.setState({ newPerson: event.target.value })
  }

  render() {
    return (
      <div>
        <h2>Puhelinluettelo</h2>
        <ul>
          {this.state.persons.map(note => <Note key={note.id} note={note} />)}
        </ul>
        <form onSubmit={this.addPerson}>
          <input 
            value={this.state.newPerson} 
            onChange={this.handleNoteChange}
          />
          <button type="submit">tallenna</button>
        </form>
      </div>
    )
  }
}

export default App
